import { eventWrapper } from '@testing-library/user-event/dist/utils'
import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const Pagination = () => {

    const [letApi, setLetApi] = useState([])
    const [currentpage, setCurrentpage] = useState(1)

    const [blogerspage, setBlogerspage] = useState(20)

    const firstpage = currentpage*blogerspage

    const lastpage = firstpage-blogerspage

    const newpeginationAnswer= letApi.slice(lastpage,firstpage)
    useEffect(() => {
      fetch('https://jsonplaceholder.typicode.com/posts')
      .then(response => response.json())
      .then(data => setLetApi(data))
    
    },[])
    
    const pageNumb=[]; 
    
    for (let i=1; i<=Math.ceil(letApi.length/blogerspage);i++){
      pageNumb.push(i)
    }
   const displayArray=(event)=>{
    setCurrentpage(event.target.innerHTML)
   }
  //  const previous=()=>{
  //   setCurrentpage(currentpage-1)
  //  }
  //   const next =()=>{
  //     setCurrentpage(currentpage+1)
  //   }
  return (

         <div>
            <div>
            {newpeginationAnswer.map(({id,title,body})=>{
            return(
                
                <div key={id}>

<Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
                    <div style={{background:'pink', textAlign:'center',fontSize:'x-small'}}>  
                    <h1>{id}</h1>
                    <h1>{title}</h1>
                    <h1>{body}</h1>
                </div>
                </div>

                

            )
         })}
         </div> 
         
         
         {
          pageNumb.map((array)=>{
            return(
              <div>
                {
                  <div style={{textAlign:'center'}}>
                
                  <button onClick={displayArray}>{array}</button>
                </div>
                }
              </div>

            )
          })
         }

         

         {/* <div >
            <button onClick={previous}>previous</button>
            <button onClick={next}>Next</button>
          </div> */}
         </div>   
         
            


    
  )
}

export default Pagination
